import React, { useState, Fragment } from 'react';
import InputWrapper, { VerifiedLabel, VerificationButton } from './Input.style';
import { FormattedMessage } from 'react-intl';

type InputProps = {
  id?: any;
  type?: 'text' | 'email' | 'password' | 'number' | 'textarea';
  disabled?: boolean;
  label?: string;
  name?: string;
  value?: any;
  style?: React.CSSProperties;
  containerStyle?: React.CSSProperties;
  className?: string;
  placeholder?: any;
  intlPlaceholderId?: string;
  intlInputLabelId?: string;
  onUpdate: Function;
  onBlur?: (e: any) => void;
  onFocus?: (e: any) => void;
  verification?: boolean; // this prop only for number field
  verified?: boolean; // this prop only for number field
  handleVerification?: (e: any) => void; // this prop only for number field
  secondaryComponent?: React.ReactChild | React.ReactChildren; // this prop only for number field
  searchIcon?: React.ReactNode;
  iconPosition?: 'left' | 'right';
};

const Input: React.FC<any> = ({
  type,
  value,
  label,
  name,
  style,
  disabled,
  className,
  onUpdate,
  placeholder,
  intlPlaceholderId,
  intlInputLabelId,
  onBlur,
  onFocus,
  verification, // this prop only for number field
  verified, // this prop only for number field
  handleVerification, // this prop only for number field
  secondaryComponent,
  searchIcon,
  iconPosition,
  containerStyle,
  ...rest
}) => {
  // Input State
  const [state, setState] = useState({
    value: value,
  });

  // Add all classs to an array
  const addAllClasses: string[] = ['field-wrapper'];

  // className prop checking
  if (className) {
    addAllClasses.push(className);
  }

  // Add disabled class
  if (disabled) {
    addAllClasses.push('disabled');
  }

  // Add with search icon class
  if (searchIcon) {
    addAllClasses.push('with-search-icon');
  }

  if (verification) {
    verified
      ? addAllClasses.push('number-verified')
      : addAllClasses.push('send-verification');
  }

  // Input onChange handler
  const handleOnChange = (e: any) => {
    let currentValue: any = e.target.value;
    setState({
      ...state,
      value: currentValue,
    });
    onUpdate(currentValue);
  };

  // Init variable for Label For and Input element
  let htmlFor, inputElement;

  // Make Label value to htmlFor
  if (label) {
    htmlFor = label.replace(/\s+/g, '_').toLowerCase();
  }

  // set icon position
  const position: string = iconPosition || 'left';

  const SearchIcon = searchIcon && (
    <span className={`${position === 'right' ? 'right' : 'left'} search-icon`}>
      {searchIcon}
    </span>
  );

  const withSearchIconClass: string =
    searchIcon && position === 'right'
      ? 'icon-right'
      : position === 'left'
      ? 'icon-left'
      : '';

  // Label Field
  const LabelField = label && (
    <label htmlFor={htmlFor}>
      <FormattedMessage
        id={intlInputLabelId ? intlInputLabelId : 'defaultIntlInputLabelId'}
        defaultMessage={label}
      />
    </label>
  );

  // Set Input element based on type prop
  switch (type) {
    case 'textarea':
      inputElement = (
        <div className='inner-wrap'>
          <FormattedMessage
            id={intlPlaceholderId ? intlPlaceholderId : 'defaultIntlId'}
            defaultMessage='&nbsp;'
          >
            {(placeholder) => (
              <textarea
                id={htmlFor}
                name={name}
                disabled={disabled}
                value={value ? value : state.value}
                onChange={handleOnChange}
                placeholder={placeholder}
                onBlur={onBlur}
                onFocus={onFocus}
                style={style}
                {...rest}
              />
            )}
          </FormattedMessage>
        </div>
      );
      break;

    case 'number':
      inputElement = (
        <div className='inner-wrap'>
          <FormattedMessage
            id={intlPlaceholderId ? intlPlaceholderId : 'defaultIntlId'}
            defaultMessage='&nbsp;'
          >
            {(placeholder) => (
              <input
                type='number'
                id={htmlFor}
                name={name}
                disabled={disabled}
                value={value ? value : state.value}
                onChange={handleOnChange}
                placeholder={placeholder}
                onBlur={onBlur}
                onFocus={onFocus}
                style={style}
                {...rest}
              />
            )}
          </FormattedMessage>
          {verification && (
            <Fragment>
              {verified ? (
                <VerifiedLabel>
                  <FormattedMessage
                    id='inputVerified'
                    defaultMessage='Verified'
                  />
                </VerifiedLabel>
              ) : (
                <VerificationButton onClick={handleVerification}>
                  <FormattedMessage
                    id='inputSendVerification'
                    defaultMessage='Send Verification'
                  />
                </VerificationButton>
              )}
            </Fragment>
          )}
        </div>
      );
      break;

    case 'password':
      inputElement = (
        <div className='inner-wrap'>
          <FormattedMessage
            id={intlPlaceholderId ? intlPlaceholderId : 'defaultIntlId'}
            defaultMessage='&nbsp;'
          >
            {(placeholder) => (
              <input
                type='password'
                id={htmlFor}
                name={name}
                disabled={disabled}
                value={value ? value : state.value}
                onChange={handleOnChange}
                // placeholder={placeholder}
                onBlur={onBlur}
                onFocus={onFocus}
                style={style}
              />
            )}
          </FormattedMessage>
        </div>
      );
      break;

    case 'email':
      inputElement = (
        <div className='inner-wrap'>
          <FormattedMessage
            id={intlPlaceholderId ? intlPlaceholderId : 'defaultIntlId'}
            defaultMessage='&nbsp;'
          >
            {(placeholder) => (
              <input
                type='email'
                id={htmlFor}
                name={name}
                disabled={disabled}
                value={value ? value : state.value}
                onChange={handleOnChange}
                placeholder={placeholder}
                onBlur={onBlur}
                onFocus={onFocus}
                style={style}
                {...rest}
              />
            )}
          </FormattedMessage>
        </div>
      );
      break;

    default:
      inputElement = (
        <div className='inner-wrap'>
          {position === 'left' && SearchIcon}
          <FormattedMessage
            id={intlPlaceholderId ? intlPlaceholderId : 'defaultIntlId'}
            defaultMessage='&nbsp;'
          >
            {(placeholder) => (
              <input
                type='text'
                id={htmlFor}
                name={name}
                value={value ? value : state.value}
                onChange={handleOnChange}
                placeholder={placeholder}
                onBlur={onBlur}
                onFocus={onFocus}
                style={style}
                className={withSearchIconClass}
                {...rest}
              />
            )}
          </FormattedMessage>
          {position === 'right' && SearchIcon}
        </div>
      );
      break;
  }

  return (
    <InputWrapper className={addAllClasses.join(' ')} style={containerStyle}>
      {LabelField || secondaryComponent ? (
        <div>
          {LabelField} {secondaryComponent}
        </div>
      ) : (
        ''
      )}
      {inputElement}
    </InputWrapper>
  );
};

export default Input;
